<?php

namespace App\Domain\ApplePay\ApplePayLib\tests\Feature;

use App\Domain\ApplePay\ApplePayLib\Decode\Decoder;
use App\Domain\ApplePay\ApplePayLib\Message\Request\DecodeRequest;
use App\Domain\ApplePay\ApplePayLib\Message\Response\DecodeResponse;
use phpseclib3\File\X509;
use Tests\TestCase;

class DecodeTest extends TestCase
{
    public function testDecodeOk()
    {
       $data = [
            'paymentData' => [
                'data' => '',
                'signature' => '',
                'header' => [
                    'publicKeyHash' => 'USXSgL3feiU5sYa3OYlWoyqXHW9fy1VS2Us1Y3qulMw=',
                    'ephemeralPublicKey' => 'MFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAE4DpxzUf/4B1eJofZ732YNcWBa1g1zF6PMMxirvGxnY5g0/cCA7TrGOj4Nf4QINEcjnjWekmb7rlgQ3nMDGcobg==',
                    'transactionId' => 'a0c9334679695d439f9a2e21a18b469ef9c6ed3a81b4dde914e99a92a1824348'
                ],
                'version' => 'EC_v1'
            ],
            'paymentMethod' => [
                'displayName' => 'MasterCard 1036',
              'network' => 'MasterCard',
              'type' => 'credit'
            ],
            'transactionIdentifier' => '9564344f17439072c1b02d4c5e9e31ef09d14b24529074d15d0f62091b035949'
        ];

        $content =  file_get_contents(storage_path('app/certificados/AppleRootCA-G3.cer'));
        $response = Decoder::make(DecodeRequest::fromArray($data), $content)->decrypt();
        $this->assertInstanceOf(DecodeResponse::class, $response);
    }
}
